import { useRef,useState,useEffect } from 'react';

const Login = () => {
    const userRef =useRef();
    const errRef = useRef();

    const [user,setUser] =useState('');
    const [pwd,setPwd] = useState('');
    const [errMsg,setErrMsg] = useState('');
    const [success, setSuccess] = useState(false);

    useEffect(()=>{
            userRef.current.focus();
    },[])

    return(
        <>
        <section className="vh-100 gradient-custom">
    <div className="container py-5 h-100">
      <div className="row d-flex justify-content-center align-items-center h-100">
        <div className="col-12 col-md-8 col-lg-6 col-xl-5">
          <div className="card bg-dark text-white" style={{borderRadius:"1rem"}}>
            <div className="card-body p-5 text-center">

              <div className="mb-md-5 mt-md-4 pb-5">

                <h2 className="fw-bold mb-2 text-uppercase">Login</h2>
                <p className="text-white-50 mb-5">Please enter your E-mail and Password!</p>

                <div className="form-outline form-white mb-4">
                  <input type="email" id="typeEmailX" className="form-control form-control-lg" placeholder="E-mail"/>
                  <label className="form-label" htmlFor="typeEmailX">Email</label>
                </div>

                <div className="form-outline form-white mb-4">
                  <input type="password" id="typePasswordX" className="form-control form-control-lg" placeholder="Password" />
                  <label className="form-label" htmlFor="typePasswordX">Password</label>
                </div>

                <p className="small mb-5 pb-lg-2"><a className="text-white-50" href="#!">Forgot password?</a></p>

                <button className="btn btn-outline-light btn-lg px-5" type="submit">Login</button>

                {/* <p className="paragraph" style={{fontSize:20,marginTop:"50px",backgroundColor:"white",color:"black"}}>This login page is used to retrieve the salary details of the employee</p> */}

                <div className="d-flex justify-content-center text-center mt-4 pt-1">
                  <a href="#!" className="text-white"><i className="fab fa-facebook-f fa-lg"></i></a>
                  <a href="#!" className="text-white"><i className="fab fa-twitter fa-lg mx-4 px-2"></i></a>
                  <a href="#!" className="text-white"><i className="fab fa-google fa-lg"></i></a>
                </div>

              </div>

              <div>
                <p className="mb-0">Don't have an account? <a href="#!" className="text-white-50 fw-bold">Sign Up</a>
                </p>
              </div>

            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
        </>
    );
}

export default Login;
