import { useEffect, useState } from "react";

import axios from 'axios';
import Navbar from "./Navbar";
import View from "./View";

const Details=()=>{
    const[res,setRes]=useState([]);
    const emp_id = JSON.parse(window.sessionStorage.getItem('emp_id'));
    const[showReceipt,setReceipt]=useState(false);
    const[month,setMonth]=useState('');






    const Fetch_data = async()=>{
        await axios.get(`http://localhost:8080/ESD_Project-1.0-SNAPSHOT/api/salary/history?employee_id=${emp_id.employee_id}`)
        .then((response)=>{
            console.log(response.data);
            setRes(response.data);
        })
        .catch(function(error)
        {
            console.log(error);
        });

    }

    useEffect(()=>{
        Fetch_data();
    },[]);

    const mystyle = {
        color: "white",
        backgroundColor: "DodgerBlue",
        padding: "10px",
        fontFamily: "Arial",
      };
    return(
        <>
        {(showReceipt === false) &&
        (
        <>
        <div className="details">
            <Navbar/>
            <table class="table">
                <thead>
                <tr>
                    <th scope ="col" style={mystyle}><b>Salary ID</b></th>
                    <th scope="col" style={mystyle}><b>Salary</b></th>
                    <th scope="col" style={mystyle}><b>Description</b></th>
                    <th scope="col" style={mystyle}><b>Payment Date</b></th>
                </tr>
                </thead>


                <tbody>
                {res.map((val, key) => {
                return (
            <tr key={key}>
              <td>{val.salaryid}</td>
              <td>{val.amount}</td>
              <td>{val.description}</td>
              <td>{val.payment_date}</td>
              <td><button type="click" onClick={
                  ()=>{
                      setReceipt(true);
                      setMonth(val.month);
                    }}>View</button></td>

            </tr>
          )
        })}
        </tbody>
            </table>
        </div>
        </>
        )
        }
        {
            (showReceipt === true) && <View employee_id={employee_id}/>
        }
        </>
    )
}

export default Details;
